    var offset = $("#left-sidebar").offset();
    var topPadding = 0;
    $(window).scroll(function() {
        if ($(window).scrollTop() > offset.top) {
            $("#left-sidebar").stop().animate({
                marginTop: $(window).scrollTop() - offset.top + topPadding
            });
        } else {
            $("#left-sidebar").stop().animate({
                marginTop: 0
            });
        };
    });
